import axios from 'axios'

export const register = newUser => {
    return axios
        .post('/register', {
            username: newUser.username,
            display_name: newUser.display_name,
            email: newUser.email,
            password: newUser.password
        })
        .then(res => {
            console.log(res)
        })
        .catch(err => {
            console.log(err)
        })
}

export const login = user => {
    return axios
        .post('/login', {
            email: user.email,
            password: user.password
        })
        .then(res => {
            localStorage.setItem('usertoken', res.data)
            return res.data
        })
        .catch(err => {
            console.log(err)
        })
}

export const addShop = shop => {
    return axios
    .post('/add-shop',{
        name: shop.restaurant_name,
        avgPrice: shop.average_price,
        type: shop.restaurant_type,
        location: shop.longitude +", " + shop.latitude,
        description: shop.description,
        menu: shop.menu_type,
        votes: shop.votes
    }).then(res => {
        console.log("Added Shop")
    })
}

export const searchAllShops = query => {
    return axios
    console.log(query)
    .get('/search-all-shops/?name='+query)
}
